# -*- coding: utf-8 -*-import jsonimport osfrom selenium.webdriver.common.by import Byfrom selenium_test.pages.basepage import BasePagefrom selenium_test.pages.homepage import HomePageclass LoginPage(BasePage):    def login(self):        # 登陆企业微信        _id = 'menu_index'        _cookie_file = './cookie.json'        file_exist = os.path.exists(_cookie_file)        # 判断cookies文件是否存在，存在则读取,不存在则保存cookies        if file_exist:            with open(_cookie_file, 'r') as f:                _cookies = json.load(f)                for cookie in _cookies:                    self.driver.add_cookie(cookie)                self.driver.refresh()            self.display_wait((By.CSS_SELECTOR, '#menu_index'))            #登录完成后返回HomePage            return HomePage(self.driver)        else:            #没有cookies文件则手动扫描登录，并保存cookies文件，返回HomePage            self.display_wait((By.CSS_SELECTOR, '#menu_index'))            _cookies = self.driver.get_cookies()            with open(_cookie_file, 'w') as f:                json.dump(_cookies, f)            return HomePage(self.driver)